    private void btnmostrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnmostrarActionPerformed
       funcionario fu = this.montarfuncionario();
       JOptionPane.showMessageDialog(null,"NomeCompelrto: "+fu.getNomeCompelrto()+"\nCpf: "+fu.getCpf()+"\nEndereco: " +
       fu.getEndereco()+"\nEmail: "+fu.getEmail()+"\nDatadeAdmissao: "+fu.getDatadeAdmissao()+"\nCargo: "+fu.getCargo()+
       "\nSalario: "+fu.getSalario()+"\nDepartamento: "+fu.getDepartamento());
       
    }//GEN-LAST:event_btnmostrarActionPerformed

    private void btngravarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btngravarActionPerformed
        funcionario fu = this.montarfuncionario();
        funcionariocontrole fuc = new funcionariocontrole();
        fuc.insert(fu);
        
    }//GEN-LAST:event_btngravarActionPerformed

