    public funcionario montarfuncionario(){
        funcionario fu = new funcionario();
        fu.setNomeCompelrto(txtfNome.getText());
        fu.setCpf(txtfCpf.getText());
        fu.setEndereco(txtfEndereco.getText());
        fu.setEmail(txtfEmail.getText());
        fu.setDatadeAdmissao(txtfDataAdm.getText());
        fu.setCargo(txtfCargo.getText());
        fu.setSalario(Float.parseFloat(txtfSalario.getText()));
        fu.setDepartamento(txtfDepartamento.getText());
        return fu;
    }
    
